import { FormEvent, useEffect, useState } from 'react';
import { getAuth, signInWithEmailAndPassword} from 'firebase/auth';
import { useHistory } from 'react-router-dom';

export const Login = () => {
    const auth = getAuth();
    const history = useHistory();
    const [ email, setEmail ] = useState('');
    const [ password, setPassword ] = useState('');

    const handleSubmit = async (event: FormEvent<HTMLFormElement>) => {
        event.preventDefault()
  
        try {
          // Send the email and password to firebase
          const userCredential = await signInWithEmailAndPassword(auth, email, password);
  
          if (userCredential) {
            history.push("/generateQRCode")
          }
        } catch (error:any) {
          console.log('User Sign In Failed', error.message);
        }
    };

    return(
        <div style={{height:'100vh', width: '100vw'}}>
            <div style={mar}>
                <div>
                <img src={process.env.PUBLIC_URL + '/images/Logo.png'} style={{width:'186px', height:'32px'}}/>
                </div>
                <div style={{fontSize: '24px', fontFamily: "LINESeedSansENBold"}}>
                    Hi there!
                </div>
                <div style={{fontSize: '14px', fontFamily: "LINESeedSansENRegular"}}>
                    Please login to continue to your account
                </div>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label className="form-label" style={{fontFamily: 'LINESeedSansENRegular'}}>
                            <div className='d-flex'>
                                <div style={{fontSize: '16px', color: '#000000'}}>
                                    Email
                                </div>
                                <div>
                                    *
                                </div>
                            </div>
                        </label>
                        <input type="email" className="form-control"  name="email" required onChange={e => setEmail(e.target.value)} value={email}  style={{width: '14vw', height: '46px'}}/>
                    </div>
                    <div>
                        <label  className="form-label" style={{fontFamily: 'LINESeedSansENRegular'}}>
                            <div className='d-flex'>
                                <div style={{fontSize: '16px', color: '#000000'}}>
                                    Password
                                </div>
                                <div>
                                    *
                                </div>
                            </div>
                        </label>
                        <input type="password" className="form-control" name="password" required onChange={e => setPassword(e.target.value)} value={password}  style={{width: '14vw', height: '46px'}}/>
                    </div>
                    <button className="btn" type="submit" style={{ width: '10vw', backgroundColor: '#059E78', height: '46px'}}>
                        <div className='d-flex justify-content-center' style={{fontFamily: "LINESeedSansENBold", color: "white"}}>
                            Log in
                        </div>
                    </button>
                </form>
            </div>
        </div>
    );
}
