import { getAuth, signInWithEmailAndPassword } from 'firebase/auth';
import { useHistory } from 'react-router-dom';

export const Login = () => {
    const auth = getAuth();
    const history = useHistory();
    const [ email, setEmail ] = useState('');
    const [ password, setPassword ] = useState('');

    const signInWithEmail_Password = async (email: string, password: string) => {
        signInWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                if(userCredential.user){
                    history.push("/generateQRCode");
                }
            })
            .catch((error) => {
                const errorCode = error.code;
                const errorMessage = error.message;
                console.log(errorMessage);
            });
    }

    return(
        <div>
            <form>
                <div>
                    <label  className="form-label">
                        <div className='d-flex'>
                            <div style={{fontSize: '16px', color: '#000000'}}>
                                รับประทานครั้งละ
                            </div>
                            <div>
                                *
                            </div>
                        </div>
                    </label>
                    <input type="text" className="form-control" aria-label="Text input with dropdown button" name="dosagePerTake" required onChange={e => setAmountOfMeds(e.target.value)} value={amountOfMeds}  style={{width: '14vw', height: '46px'}}/>
                </div>
                <div>
                    <label  className="form-label">
                        <div className='d-flex'>
                            <div style={{fontSize: '16px', color: '#000000'}}>
                                รับประทานครั้งละ
                            </div>
                            <div>
                                *
                            </div>
                        </div>
                    </label>
                    <input type="text" className="form-control" aria-label="Text input with dropdown button" name="dosagePerTake" required onChange={e => setAmountOfMeds(e.target.value)} value={amountOfMeds}  style={{width: '14vw', height: '46px'}}/>
                </div>
            </form>
        </div>
    );
}
